  `props` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否匹配props',
  `org_type` char(1) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '0' COMMENT '组织类型',
  `link_type` char(1) COLLATE utf8mb4_general_ci NOT NULL DEFAULT '0' COMMENT '链接类型',
  `link_url` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '链接url',
  `status` char(1) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '0' COMMENT '状态, 0:正常，9:禁用',
  `created_at` datetime DEFAULT NULL COMMENT '创建日期',
  `updated_at` datetime DEFAULT NULL COMMENT '更新日期',
  `deleted_at` datetime DEFAULT NULL COMMENT '删除日期',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `tenant-code` (`tenant_id`,`route_name`) USING BTREE COMMENT '编码'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

